query:
UPDATE `simulations` SET `valorAporte` = `valorInvestido`, `valorDesejado` = `valorTotalOferta`;
UPDATE `simulations` SET `taxaMensal` = ROUND(`taxaJurosAa` / 12);
UPDATE `simulations` SET `sistemaAmortizacao` = 'LINEAR' WHERE `amortizacaoMetodo` = 'linear';
UPDATE `simulations` SET `sistemaAmortizacao` = 'BULLET' WHERE `amortizacaoMetodo` = 'bullet';
UPDATE `simulations` SET `tipoSimulacao` = 'investimento';
UPDATE `simulations` SET `possuiCarencia` = 1 WHERE `carenciaJurosMeses` > 0 OR `carenciaPrincipalMeses` > 0;
UPDATE `simulations` SET `mesesCarencia` = GREATEST(`carenciaJurosMeses`, `carenciaPrincipalMeses`) WHERE `possuiCarencia` = 1;

